(function() {

  $('form.result').on('updated.cc', function() {

    $('select.crosscompute-select').each(function() {
      var $o = $(this), key = $o.attr('id'), lines = [];
      $.each($o.select2('data'), function(i, x) {
        lines.push(x.id);
      });
      $('[name=' + key + '_txt]').val(lines.join('\n'));
    });

  });

}());
